CREATE OR REPLACE TABLE SWB_SECURITY_STG (
	PROCESSID NUMBER(10,0),
	CUSTODIAN_NAME VARCHAR(10),
	RUN_ID NUMBER(10,0),
	DATA VARCHAR(16777216),
	CREATEDATE TIMESTAMP_LTZ(3) NOT NULL DEFAULT CAST(CURRENT_TIMESTAMP() AS TIMESTAMP_LTZ(3)),
	FILE_NAME VARCHAR(255)
);

