CREATE OR REPLACE FILE FORMAT HT_SOURCE_FILEFORMAT TYPE=CSV FIELD_DELIMITER = '`'
                  ERROR_ON_COLUMN_COUNT_MISMATCH = FALSE;
                  
CREATE OR REPLACE STAGE PRSH_STAGE
        FILE_FORMAT = (FORMAT_NAME= 'HT_SOURCE_FILEFORMAT')
        COPY_OPTIONS = (ON_ERROR  = CONTINUE);  
